import { React, useState, useEffect } from 'react'
import SimpleImageSlider from "react-simple-image-slider";

export default function Photo(props) {
    const [album, setAlbum] = useState([]);

    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/photos/?albumId=${props.album}`)
            .then(response => { return response.json() })
            .then(data => {
                let albums=[];
                data.forEach(photo => {
                    albums.push({url:`${photo.thumbnailUrl}`})
                });
                setAlbum(albums)
                // console.log(album)
                // setAlbum(data.map(photo => {
                //     return ({url:`${photo.thumbnailUrl}`})
                // })); 
            }
            ).catch(()=>{})
    },[])

    return (
        album&&album.length&&
        <div>
        <SimpleImageSlider
          width={1408}
          height={200}
          images={album}
          showNavs
          showBullets
          navStyle
        />
      </div>
    
    
    )
}
