import './styleLogIn.css';
import { React, useState } from 'react'
import { useHistory } from 'react-router-dom';


function Login() {
  const [name, setName] = useState("Bret");
  const [password, setPassword] = useState("3159");
  const history = useHistory()

  const handleChange = (func, value) => {
    func(value);
  }

  const checkUser = event => {
    event.preventDefault();
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      }).then(async (data) => {
        if (data) {
          const exist = await check(data);
          if (!exist) {
            alert("the userName is not correct please try again");
            setName('');
            setPassword('')
          }
        }
      }
      )
  }

  const check = data => {
    let isExist = false;
    data.forEach(user => {
      if (user.username === name) {
        if (password === user.address.geo.lat.split('.')[1]) {
          localStorage.setItem('user', JSON.stringify(user))
          history.push('/application');
          isExist = true;
        }
      }
    })
    return isExist;
  }

  return (
    <div className="from">
      <div className="left">
        <img src="../component/beach."></img>
      </div>
      <div className="right">
        <h1 className="h1">welcome</h1>
        <p className="p">Welcome back! Log in to your account to view today's updates:</p>
        <div className="form">


          <form  onSubmit={(event) => checkUser(event)}>
            <input type="text" placeholder="Email" value={name} onChange={(event) => handleChange(setName, event.target.value)} className="input"/>
            <input type="password" placeholder="Password" value={password} onChange={(event) => handleChange(setPassword, event.target.value)} className="input"/>
            <button type='submit' className="button">Log in</button>
          </form>
        </div>
      </div>
    </div>

  );
}

export default Login;
