import { React, useState, useEffect } from 'react'
import Photo from "./Photo";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch
} from "react-router-dom";


function Albums() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [albums, setAlbums] = useState([]);
  const [currentAlbum , setCurrentAlbum] = useState(null)
  let { url } = useRouteMatch();

 

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/albums/?userId=${user.id}`)
      .then(response => {
        if (response.ok) {
          return response.json();
        }
      })
      .then( data => {
        
        setAlbums(data.map(album => {
          return (<div ><Link  to={`/application/albums/${album.id}`} onClick={()=>{setCurrentAlbum(album.id)}}>{album.title}  </Link><br /></div>);
        }))

      });

  }
    , [user]);
  return (
    <Router>
      <Switch>
        <Route exact path={`${url}`}>
          {albums}
        </Route  >
        <Route path={`${url}`} >
          <Photo album = {currentAlbum}/>
        </Route  >
      </Switch>


    </Router>


  )
}
export default Albums;
