import { useState, useEffect } from 'react'
import ListItemIcon from '@material-ui/core/ListItemIcon';
import AttachmentOutlinedIcon from '@material-ui/icons/AttachmentOutlined';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';



import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import Checkbox from '@material-ui/core/Checkbox';



const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  button: {
    display: 'block',
    marginTop: theme.spacing(2),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

export default function Todos() {
  const classes = useStyles();
  const [age, setAge] = React.useState('');
  const [open, setOpen] = React.useState(false);
  const [select, setSelect] = useState('serial');
  const [information, setInformation] = useState(null);
  const user = JSON.parse(localStorage.getItem('user'));

  const handleChange = (event) => {
    setAge(event.target.value);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };


  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/todos/?userId=${user.id}`)
      .then(response => response.json()
        .then((data) => {

          setInformation(data)
        }))
      .catch(() => { })

  }, [user.id])


  const serial = () => {

    let data = information;
    data.sort((a, b) => {
      if (a.id < b.id)
        return -1
      else
        return 1;
    }
    )
    setInformation(data);


  }
  const alphaBeit = () => {
    let data = information;
    data.sort((a, b) => {
      if (a.title < b.title)
        return -1
      else
        return 1;
    }
    );
    setInformation(data);

  }

  const complete = () => {
    let notChecked = []
    let checked = [];
    for (let i = 0; i < information.length; ++i) {
      if (information[i].completed) {
        checked.push(information[i]);
      } else {
        notChecked.push(information[i])
      }

    }
    let data = [...checked, ...notChecked];
    setInformation(data)

  }

  const random = () => {
    let data = information;
    for (let i = 0; i < data.length; ++i) {
      let randomNum = Math.floor(Math.random() * data.length);
      let num = data[i];
      data[i] = data[randomNum];
      data[randomNum] = num;
    }
    setInformation(data)

  }




  const handleOnChange = (e) => {
    let value = e.target.value;
    setSelect(value);
    switch (value) {
      case "alphaBeit":
        alphaBeit();
        break;
      case "complete":
        complete();
        break;
      case "random":
        random();
        break;
      default:
        serial();
        break;
    }
  }


  return (
    <div>
      <div>
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-controlled-open-select-label" variant="h5">sort by</InputLabel>
          <Select
            labelId="demo-controlled-open-select-label"
            id="demo-controlled-open-select"
            open={open}
            onClose={handleClose}
            onOpen={handleOpen}
            value={select} onChange={(e) => handleOnChange(e)}
          >
            <MenuItem value="serial">serial</MenuItem>
            <MenuItem value="alphaBeit">alphaBeit</MenuItem>
            <MenuItem value="complete">complete</MenuItem>
            <MenuItem value="random">random</MenuItem>
          </Select>
        </FormControl>
      </div>
      <List dense className={classes.root}>
        {information && (information.map((value) => {
          const labelId = `checkbox-list-secondary-label-${value}`;
          return (
            <ListItem key={value} button>
              <ListItemIcon>
                <AttachmentOutlinedIcon />
              </ListItemIcon>
              <ListItemText id={labelId} primary={value.title} />
              <ListItemSecondaryAction>
                <Checkbox icon={<FavoriteBorder />} checkedIcon={<Favorite />} name="checkedH" edge="end"
                  checked={value.completed}
                  inputProps={{ 'aria-labelledby': labelId }} />
              </ListItemSecondaryAction>
            </ListItem>
          );
        }))}
      </List>
    </div>
  );
}
