import { React, useState } from 'react'
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import AccountCircle from '@material-ui/icons/AccountCircle';

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
}));


function Login() {

  const classes = useStyles();

  const [name, setName] = useState("Bret");
  const [password, setPassword] = useState("3159");
  const history = useHistory()

  const handleChange = (func, value) => {
    func(value);
  }

  const checkUser = event => {
    event.preventDefault();
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      }).then(async (data) => {
        if (data) {
          const exist = await check(data);
          if (!exist) {
            alert("the userName is not correct please try again");
            setName('');
            setPassword('')
          }
        }
      }
      )
  }

  const check = data => {
    let isExist = false;
    data.forEach(user => {
      if (user.username === name&&password === user.address.geo.lat.split('.')[1])  {
          localStorage.setItem('user', JSON.stringify(user))
          history.push('/application');
          isExist = true;
        
      }
    })
    return isExist;
  }

  return (
    <div className="from">
          <form  onSubmit={(event) => checkUser(event)}>
            <input type="text" placeholder="Email" value={name} onChange={(event) => handleChange(setName, event.target.value)} className="input"/>
            <input type="password" placeholder="Password" value={password} onChange={(event) => handleChange(setPassword, event.target.value)} className="input"/>
            <button type='submit' className="button">Log in</button>
          </form>
          <div>
      <FormControl className={classes.margin}>
        <InputLabel htmlFor="input-with-icon-adornment">With a start adornment</InputLabel>
        <Input
          id="input-with-icon-adornment"
          startAdornment={
            <InputAdornment position="start">
              <AccountCircle />
            </InputAdornment>
          }
        />
      </FormControl>
      <TextField
        className={classes.margin}
        id="input-with-icon-textfield"
        label="TextField"
        InputProps={{
          startAdornment: (
            <InputAdornment position="start">
              <AccountCircle />
            </InputAdornment>
          ),
        }}
      />
      <div className={classes.margin}>
        <Grid container spacing={1} alignItems="flex-end">
          <Grid item>
            <AccountCircle />
          </Grid>
          <Grid item>
            <TextField id="input-with-icon-grid" label="With a grid" />
          </Grid>
        </Grid>
      </div>
    </div>
    </div>

  );
}

export default Login;
