import { React, useState, useEffect } from 'react'
import Photo from "./Photo";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useRouteMatch
} from "react-router-dom";


function Albums() {
  const user = JSON.parse(localStorage.getItem('user'));
  const [albums, setAlbums] = useState([]);
  const [currentAlbum , setCurrentAlbum] = useState(null)
  let { url } = useRouteMatch();

 
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/albums/?userId=${user.id}`)
      .then(response => {
        if (response.ok) {
          return response.json();
        }
      })
      .then( data => {
        data.sort((a, b) => {
          if (a.title < b.title)
              return -1
          else
              return 1;
      }
      );
        setAlbums(data.map(album => {
          return (<div key={album.id}><Link  to={`/application/albums/${album.id}`} onClick={()=>{setCurrentAlbum(album.id)}}>{album.title}  </Link><br /></div>);
        }))

      });

  }
    , [user]);
  return (
  
      <Switch>
        <Route exact path={`${url}`}>
          {albums}
        </Route  >
        <Route path={`${url}`} >
          <Photo album = {currentAlbum}/>
        </Route  >
      </Switch>





  )
}
export default Albums;
