                            return (
                                <Grid item xs={12}>
                                    <Paper className={classes.paper}>
                                        <ListItem button >
                                            <Link onClick={() => props.setPost(post)} to={`${url}/${post.id}`}>
                                                <ListItemIcon>
                                                    <PlaylistAddSharpIcon color="secondary" />
                                                </ListItemIcon>
                                            </Link>
                                            <ListItemText primary={post.title} />
                                        </ListItem>
                                    </Paper>
                                </Grid>
