import {
    Route,
    Link,
    useRouteMatch,
} from "react-router-dom";

import Comments from './Comments'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});

function Posts(props) {
    const [contact, setContact] = useState(null);
    let { url } = useRouteMatch();
    useEffect(() => {
            setContact(props.post);
    }, [])
    const classes = useStyles();
    const bull = <span className={classes.bullet}>•</span>;

    return (
        <div>
            <Card className={classes.root}>
        <CardContent>
          <Typography className={classes.title} color="textSecondary" gutterBottom>
          <Link to='/application/posts'>all</Link>
          </Typography>
          <Link onClick={() => props.setPost(post)} to={`${url}/${post.id}`}>
                                    <ListItemIcon>
                                        <PlaylistAddSharpIcon />
                                    </ListItemIcon>
                                </Link>
          <Typography variant="h5" component="h2">
          {contact && (<div>
                <h3>{contact.title}</h3>
                <p>{contact.body}</p>
            </div>)}
          </Typography>
          <Typography className={classes.pos} color="textSecondary">
          <Link to={`${url}/${props.post.id}/comments`}>to see my comments</Link>
          </Typography>
        </CardContent>
      </Card>
            <Route path={`/application/posts/${props.post.id}/comments`}>
                <Comments postId={props.post.id} />
            </Route>
        </div>

       

    )
}
export default Posts;
