import { React, useState, useEffect } from 'react'
import SimpleImageSlider from "react-simple-image-slider";

export default function Photo(props) {
    const [album, setAlbum] = useState([]);

    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/photos/?albumId=${props.album}`)
            .then(response => { return response.json() })
            .then(data => {
                let albums = [];
                data.forEach(photo => {
                    albums.push({ url: `${photo.thumbnailUrl}` })
                });
                setAlbum(albums)
            }
            )
    }, [props.album])

    return (<div >
        {album.length ?
            <p>Loading....</p> :
            <SimpleImageSlider
                width={1400}
                height={800}
                images={album}
                showNavs
                showBullets

            />
        }

    </div>
    )
}
