import { React, useState } from 'react'
import { useHistory } from 'react-router-dom';

function Login() {
  const [name, setName] = useState("Bret");
  const [password, setPassword] = useState("3159");
  const history = useHistory()

  const handleChange = (func, value) => {
    func(value);
  }

  const checkUser = event => {
    event.preventDefault();
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      }).then(async(data) => {
        if (data) {
          const exist=await check(data);
          if (!exist) {
            alert("the userName is not correct please try again");
            setName('');
            setPassword('')
          }
        }
      }
      )
  }

  const check = data => {
    let isExist=false;
    data.forEach(user => {
      if (user.username === name) {
        if (password === user.address.geo.lat.split('.')[1]) {
           localStorage.setItem('user', JSON.stringify(user))
          history.push('/application');
          isExist=true;
        }
      }
    })
    return isExist;
  }

  return (
    <div >
      <form onSubmit={(event) => checkUser(event)}>
        <input type='text' value={name} onChange={(event) => handleChange(setName, event.target.value)} placeholder="name" required />
        <input type='password' value={password} onChange={(event) => handleChange(setPassword, event.target.value)} placeholder="password" required />
        <button type='submit'>log in</button>
      </form>
    </div>
  );
}

export default Login;

