import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Checkbox from '@material-ui/core/Checkbox';
import Avatar from '@material-ui/core/Avatar';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
}));

export default function Todos() {
  const classes = useStyles();
  const [checked, setChecked] = React.useState([1]);

      const [contact, setContact] = useState(null);
    const [select, setSelect] = useState('serial');
    const [information, setInformation] = useState(null);
    const user = JSON.parse(localStorage.getItem('user'));
    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/todos/?userId=${user.id}`)
            .then(response => response.json()
                .then((data) => {
                    mapy(data);
                    setInformation(data)
                }))
                .catch(() =>{})

    }, [user.id])


    const serial = () => {

        let data = information;
        data.sort((a, b) => {
            if (a.id < b.id)
                return -1
            else
                return 1;
        }
        )
        setInformation(data);
        mapy(information)

    }
    const alphaBeit = () => {
        let data = information;
        data.sort((a, b) => {
            if (a.title < b.title)
                return -1
            else
                return 1;
        }
        );
        setInformation(data);
        mapy(information)
    }

    const complete = () => {
        let notChecked = []
        let checked = [];
        for (let i = 0; i < information.length; ++i) {
            if (information[i].completed) {
                checked.push(information[i]);
            } else {
                notChecked.push(information[i])
            }

        }
        let data = [...checked, ...notChecked];
        setInformation(data)
        mapy(data);
    }

    const random = () => {
        let data = information;
        for (let i = 0; i < data.length; ++i) {
            let randomNum = Math.floor(Math.random() * data.length);
            let num = data[i];
            data[i] = data[randomNum];
            data[randomNum] = num;
        }
        setInformation(data)
        mapy(data);
    }

    const mapy = (data) => {
        let ul = data.map(todo => {
            return (<p key={todo.id}>
                <input type='checkbox' checked={todo.completed} />
                <span>{todo.title}</span>
            </p>)
        })
        setContact(ul)
    }


    const handleOnChange = (e) => {
        let value = e.target.value;
        setSelect(value);
        switch (value) {
            case "alphaBeit":
                alphaBeit();
                break;
            case "complete":
                complete();
                break;
            case "random":
                random();
                break;
            default:
                serial();
                break;
        }
    }

  const handleToggle = (value) => () => {
    const currentIndex = checked.indexOf(value);
    const newChecked = [...checked];

    if (currentIndex === -1) {
      newChecked.push(value);
    } else {
      newChecked.splice(currentIndex, 1);
    }

    setChecked(newChecked);
  };

  return (
    <List dense className={classes.root}>
      {(information.map((value) => {
        const labelId = `checkbox-list-secondary-label-${value}`;
        return (
          <ListItem key={value} button>
            <ListItemAvatar>
              <Avatar
                alt={`Avatar n°${value + 1}`}
                src={`/static/images/avatar/${value + 1}.jpg`}
              />
            </ListItemAvatar>
            <ListItemText id={labelId} primary={value.title} />
            <ListItemSecondaryAction>
              <Checkbox
                edge="end"
                checked={value.completed}
                inputProps={{ 'aria-labelledby': labelId }}
              />
            </ListItemSecondaryAction>
          </ListItem>
        );
      }))}
    </List>
  );
}
