import { React, useState } from 'react'
import { useHistory } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Input from '@material-ui/core/Input';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormControl from '@material-ui/core/FormControl';
import AccountCircle from '@material-ui/icons/AccountCircle';
import LockOpenIcon from '@material-ui/icons/LockOpen';
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import CssBaseline from '@material-ui/core/CssBaseline';
import useScrollTrigger from '@material-ui/core/useScrollTrigger';
import Box from '@material-ui/core/Box';
import Container from '@material-ui/core/Container';

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
}));


function Login() {

  const classes = useStyles();
  const [name, setName] = useState("Bret");
  const [password, setPassword] = useState("3159");
  const history = useHistory()

  const handleChange = (func, value) => {
    func(value);
  }

  const checkUser = event => {
    event.preventDefault();
    fetch('https://jsonplaceholder.typicode.com/users')
      .then(response => {
        if (response.ok) {
          return response.json()
        }
      }).then(async (data) => {
        if (data) {
          const exist = await check(data);
          if (!exist) {
            alert("the userName is not correct please try again");
            setName('');
            setPassword('')
          }
        }
      }
      )
  }

  const check = data => {
    let isExist = false;
    data.forEach(user => {
      if (user.username === name && password === user.address.geo.lat.split('.')[1]) {
        localStorage.setItem('user', JSON.stringify(user))
        history.push('/application');
        isExist = true;

      }
    })
    return isExist;
  }

  return (
    <div className="from">
      

      <CssBaseline />
        <AppBar>
          <Toolbar>
            <Typography variant="h6">hello please log in first</Typography>
          </Toolbar>
        </AppBar>
      <Toolbar />
      <Container>
        <Box my={2}>
        <form onSubmit={(event) => checkUser(event)}>
        <FormControl className={classes.margin} >
          <Input type="text" placeholder="Email" value={name} onChange={(event) => handleChange(setName, event.target.value)}
            id="input-with-icon-adornment"
            startAdornment={
              <InputAdornment position="start">
                <AccountCircle />
              </InputAdornment>
            }
          />
          <Input type="password" placeholder="Password" value={password} onChange={(event) => handleChange(setPassword, event.target.value)}
            id="input-with-icon-adornment"
            startAdornment={
              <InputAdornment position="start">
                <LockOpenIcon />
              </InputAdornment>
            }
          />
        <Button variant="contained" color="primary" type='submit'> Log in</Button>
        </FormControl>
      </form>
        </Box>
      </Container>

    </div>
  );
}

export default Login;
